import React, { useState } from 'react';

const PropertyPosting = () => {
  const [propertyType, setPropertyType] = useState('');
  const [price, setPrice] = useState('');
  const [finalPrice, setFinalPrice] = useState('');
  const [contactDetails, setContactDetails] = useState('');
  const [location, setLocation] = useState('');
  const [bedrooms, setBedrooms] = useState('');
  const [bhk, setBhks] = useState('');
  const [parkingType, setParkingType] = useState('');
  const [images, setImages] = useState([]); // State for multiple uploaded images
  const [message, setMessage] = useState('');

  const handlePost = () => {
    // Validate required fields
    if (!propertyType || !price || !finalPrice || !contactDetails || !location) {
      setMessage('Please fill in all fields.');
      return;
    }

    // Prepare property object to post
    const property = { 
      propertyType, 
      price: parseFloat(price), 
      finalPrice: parseFloat(finalPrice), 
      contactDetails, 
      location,
      bedrooms: propertyType === 'Apartment' || propertyType === 'House' ? bedrooms : undefined,
      bhk: propertyType === 'Apartment' || propertyType === 'House' ? bhk : undefined,
      parkingType: propertyType === 'Apartment' || propertyType === 'House' ? parkingType : undefined,
      images, // Include uploaded images
    };

    console.log('Property posted:', property);
    setMessage('Property posted successfully!');

    // Clear the form fields after posting
    setPropertyType('');
    setPrice('');
    setFinalPrice('');
    setContactDetails('');
    setLocation('');
    setBedrooms('');
    setBhks('');
    setParkingType('');
    setImages([]); // Clear uploaded images
  };

  // Handle image file change
  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 10) {
      alert('You can upload a maximum of 10 images.');
      return;
    }

    // Check file sizes
    const validFiles = files.filter(file => file.size <= 5 * 1024 * 1024); // Maximum 5MB per image
    if (validFiles.length !== files.length) {
      alert('Some files are too large. Maximum file size is 5MB.');
      return;
    }

    // Create previews and set images state
    const imagePreviews = validFiles.map(file => URL.createObjectURL(file)); 
    setImages(imagePreviews); // Set image previews
  };

  return (
    <div className="property-posting-container">
      <h2>Post a Property</h2>
      
      <select
        value={propertyType}
        onChange={(e) => setPropertyType(e.target.value)}
      >
        <option value="">Select Property Type</option>
        <option value="Apartment">Apartment</option>
        <option value="House">House</option>
        <option value="Commercial">Commercial</option>
        <option value="Plot">Plot</option>
      </select>
      
      {propertyType === 'Apartment' || propertyType === 'House' ? (
        <>
          <input
            type="number"
            placeholder="Number of Bedrooms"
            value={bedrooms}
            onChange={(e) => setBedrooms(e.target.value)}
          />
          <select
            value={bhk}
            onChange={(e) => setBhks(e.target.value)}
          >
            <option value="">Select BHK</option>
            <option value="1 BHK">1 BHK</option>
            <option value="2 BHK">2 BHK</option>
            <option value="3 BHK">3 BHK</option>
            <option value="4 BHK">4 BHK</option>
            <option value="5 BHK">5 BHK</option>
          </select>

          <select
            value={parkingType}
            onChange={(e) => setParkingType(e.target.value)}
          >
            <option value="">Select Parking Type</option>
            <option value="Side">Side</option>
            <option value="Front">Front</option>
            <option value="Cellar">Cellar</option>
          </select>
        </>
      ) : null}

      <input
        type="number"
        placeholder="Price"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
      />
      <input
        type="number"
        placeholder="Final Price"
        value={finalPrice}
        onChange={(e) => setFinalPrice(e.target.value)}
      />
      <input
        type="text"
        placeholder="Contact Details"
        value={contactDetails}
        onChange={(e) => setContactDetails(e.target.value)}
      />
      <input
        type="text"
        placeholder="Location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />
      
      {/* Image Upload Section */}
      <input
        type="file"
        accept="image/*"
        multiple
        onChange={handleImageChange}
        required
      />
      {images.length > 0 && (
        <div className="image-preview">
          {images.map((image, index) => (
            <img key={index} src={image} alt={`Property Preview ${index + 1}`} style={{ width: '100px', height: 'auto', margin: '10px' }} />
          ))}
        </div>
      )}

      <button onClick={handlePost}>Post</button>
      {message && <p className="feedback-message">{message}</p>}
    </div>
  );
};

export default PropertyPosting;
