// PropertyList.jsx
import React from 'react';

const PropertyList = ({ properties, onUploadImages }) => {
  const handleImageUpload = (id) => {
    const input = document.getElementById(`image-upload-${id}`);
    const files = Array.from(input.files);
    const imageUrls = files.map(file => URL.createObjectURL(file)); // Create URLs for uploaded images
    onUploadImages(id, imageUrls); // Call the parent function to update images
  };

  return (
    <div className="property-list">
      {properties.length > 0 ? (
        properties.map((property) => (
          <div key={property.id} className="property-item">
            <h2>{property.name}</h2>
            <p><strong>Location:</strong> {property.location}</p>
            <p><strong>Type:</strong> {property.type}</p>
            <div className="statistics">
              <div><strong>Price:</strong> ${property.price.toLocaleString()}</div>
              <div><strong>Size:</strong> {property.size} sqft</div>
              <div><strong>Bedrooms:</strong> {property.bedrooms}</div>
              <div><strong>Bathrooms:</strong> {property.bathrooms}</div>
              <div><strong>Parking:</strong> {property.parking ? 'Yes' : 'No'}</div>
            </div>
            <div className="image-gallery">
              {property.images.length > 0 ? (
                property.images.map((image, index) => (
                  <img
                    key={index}
                    src={image}
                    alt={`${property.name} - Image ${index + 1}`} // Removed "image" from alt text
                    style={{ width: '100px', height: 'auto', margin: '5px' }}
                  />
                ))
              ) : (
                <p>No images available</p>
              )}
            </div>
            <input 
              type="file" 
              multiple 
              id={`image-upload-${property.id}`} 
              accept="image/*" 
              onChange={() => handleImageUpload(property.id)} 
            />
          </div>
        ))
      ) : (
        <p>No properties found matching your criteria.</p>
      )}
    </div>
  );
};

export default PropertyList;
