// PropertiesPage.js
import React, { useState } from 'react';
 // Import your properties list
import SearchBar from './SearchBar';
import PropertyList from './PropertyList';
import './PropertiesPage.css'; // Import your CSS

const PropertiesPage = () => {
  const [filteredProperties, setFilteredProperties] = useState(properties);

  const handleSearch = (params) => {
    let filtered = properties;

    // Filter by location
    if (params.location) {
      filtered = filtered.filter(property => property.location === params.location);
    }

    // Filter by property type
    if (params.propertyType) {
      filtered = filtered.filter(property => property.propertyType === params.propertyType);
    }

    // Filter by minimum price
    if (params.minPrice) {
      filtered = filtered.filter(property => property.price >= params.minPrice);
    }

    // Filter by maximum price
    if (params.maxPrice) {
      filtered = filtered.filter(property => property.price <= params.maxPrice);
    }

    // Filter by number of bedrooms
    if (params.bedrooms) {
      filtered = filtered.filter(property => property.bedrooms === Number(params.bedrooms));
    }

    // Update state with the filtered properties
    setFilteredProperties(filtered);
  };

  return (
    <div className="properties-page">
      <h1>Properties</h1>
      <SearchBar onSearch={handleSearch} />
      <PropertyList properties={filteredProperties} onViewMore={(property) => console.log(property)} />
    </div>
  );
};

export default PropertiesPage;
