// PropertyListContainer.jsx
import React, { useState } from 'react';
import SearchBar from './SearchBar';
import PropertyList from './PropertyList';


const PropertyListContainer = () => {
  // Sample static properties
  const allProperties = [
    {
      id: 1,
      name: 'Modern House',
      location: 'Hyderabad',
      type: 'House',
      price: 1200000,
      size: 3000,
      bedrooms: 4,
      bathrooms: 3,
      images: [], // Added images property
      parking: false, // Added parking property
    },
    {
      id: 2,
      name: 'Luxury Apartment',
      location: 'Nellore',
      type: 'Apartment',
      price: 950000,
      size: 2000,
      bedrooms: 3,
      bathrooms: 2,
      images: [],
      parking: false,
    },
    {
      id: 3,
      name: 'Beach Condo',
      location: 'Khammam',
      type: 'Plot',
      price: 750000,
      size: 1800,
      bedrooms: 2,
      bathrooms: 2,
      images: [],
      parking: false,
    },
    {
      id: 4,
      name: 'Commercial Space',
      location: 'Hyderabad',
      type: 'Commercial',
      price: 1500000,
      size: 4000,
      bedrooms: 0,
      bathrooms: 1,
      images: [],
      parking: false,
    },
  ];

  const [properties, setProperties] = useState(allProperties);
  const [filteredProperties, setFilteredProperties] = useState(allProperties);

  const handleSearch = (filters) => {
    const { location, propertyType } = filters;

    const filtered = properties.filter((property) => {
      const locationMatch = location ? property.location === location : true;
      const typeMatch = propertyType ? property.type === propertyType : true;
      return locationMatch && typeMatch;
    });

    setFilteredProperties(filtered);
  };

  const handleAddProperty = (newProperty) => {
    // Create a new property with a unique ID
    const newId = properties.length ? properties[properties.length - 1].id + 1 : 1;
    const propertyWithId = { id: newId, ...newProperty };

    setProperties((prevProperties) => [...prevProperties, propertyWithId]);
    setFilteredProperties((prevFiltered) => [...prevFiltered, propertyWithId]);
  };

  return (
    <div>
      <SearchBar onSearch={handleSearch} />
      <AddProperty onAddProperty={handleAddProperty} />
      <PropertyList properties={filteredProperties} />
    </div>
  );
};

export default PropertyListContainer;
