import React, { useState, useEffect } from 'react';
import PropertyListing from './PropertyListing';

const PropertySearchPage = () => {
  const [properties, setProperties] = useState([]);

  useEffect(() => {
    const dummyProperties = [
      {
        id: 1,
        title: 'Luxury Apartment in Hyderabad',
        location: 'Hyderabad',
        price: 5000000,
        propertyType: 'Apartment/House',
        bedrooms: 3,
        image: '/images/property1.jpg'
      },
      {
        id: 2,
        title: 'Commercial Plot in Khammam',
        location: 'Khammam',
        price: 2000000,
        propertyType: 'Commercial',
        bedrooms: 0,
        image: '/images/property2.jpg'
      }
    ];

    // Replace 'filters' with hardcoded values or dynamically adjust them
    const filters = {
      location: 'Hyderabad', // Example filter
      minPrice: 1000000,
      maxPrice: 6000000,
      propertyType: 'Apartment/House',
      bedrooms: 3
    };

    const filteredProperties = dummyProperties.filter((property) => {
      return (
        (filters.location ? property.location === filters.location : true) &&
        (filters.minPrice ? property.price >= filters.minPrice : true) &&
        (filters.maxPrice ? property.price <= filters.maxPrice : true) &&
        (filters.propertyType ? property.propertyType === filters.propertyType : true) &&
        (filters.bedrooms ? property.bedrooms === Number(filters.bedrooms) : true)
      );
    });

    setProperties(filteredProperties);
  }, []);

  return (
    <div>
      <PropertyListing properties={properties} />
    </div>
  );
};

export default PropertySearchPage;
