import React from 'react';
import PropTypes from 'prop-types';

const PropertyList = ({ properties, onUploadImages }) => {
  const handleImageUpload = (id) => {
    const input = document.createElement('input');
    input.type = 'file';
    input.multiple = true;
    input.accept = 'image/*';
    
    input.onchange = async (event) => {
      const files = event.target.files;
      const newImages = [];

      for (const file of files) {
        const reader = new FileReader();
        reader.onload = (e) => {
          newImages.push(e.target.result);
        };
        reader.readAsDataURL(file);
      }

      // Wait until all images are read before uploading
      await new Promise((resolve) => {
        reader.onloadend = () => {
          resolve();
        };
      });

      if (newImages.length > 0) {
        onUploadImages(id, newImages);
      }
    };

    input.click();
  };

  return (
    <div className="property-list">
      {properties.map((property) => (
        <div key={property.id} className="property-card">
          <h2>{property.name}</h2>
          <p>Location: {property.location}</p>
          <p>Type: {property.type}</p>
          <p>Price: â‚¹{property.price}</p>
          <p>Size: {property.size} sqft</p>
          <p>Bedrooms: {property.bedrooms}</p>
          <p>Bathrooms: {property.bathrooms}</p>
          <p>Parking: {property.parking ? 'Yes' : 'No'}</p>
          <div className="property-images">
            {property.images.map((image, index) => (
              <img key={index} src={image} alt={`${property.name} ${index}`} />
            ))}
          </div>
          <button onClick={() => handleImageUpload(property.id)}>Upload Images</button>
        </div>
      ))}
    </div>
  );
};

PropertyList.propTypes = {
  properties: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number.isRequired,
      name: PropTypes.string.isRequired,
      location: PropTypes.string.isRequired,
      type: PropTypes.string.isRequired,
      price: PropTypes.number.isRequired,
      size: PropTypes.number.isRequired,
      bedrooms: PropTypes.number.isRequired,
      bathrooms: PropTypes.number.isRequired,
      images: PropTypes.arrayOf(PropTypes.string).isRequired,
      parking: PropTypes.bool.isRequired,
    })
  ).isRequired,
  onUploadImages: PropTypes.func.isRequired,
};

export default PropertyList;
