import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import PropertyList from './PropertyList';
import SearchBar from './SearchBar';
import PropertyDetails from './PropertyDetails';

const PropertyListContainer = () => {
  const [properties] = useState([
    { id: 1, title: 'Modern Villa', location: 'Los Angeles', price: 500000, bedrooms: 3, bathrooms: 2, parking: 2, image: require('../assets/1.jpeg') },
    { id: 2, title: 'Cozy Apartment', location: 'New York', price: 300000, bedrooms: 2, bathrooms: 1, parking: 1, image: require('../assets/2.jpeg') },
    { id: 3, title: 'Luxury House', location: 'Miami', price: 400000, bedrooms: 4, bathrooms: 3, parking: 2, image: require('../assets/3.jpeg') },
    { id: 4, title: 'Spacious Condo', location: 'Seattle', price: 750000, bedrooms: 3, bathrooms: 2, parking: 2, image: require('../assets/4.jpeg') },
    { id: 5, title: 'Charming Bungalow', location: 'Chicago', price: 650000, bedrooms: 3, bathrooms: 1, parking: 1, image: require('../assets/5.jpeg') },
    { id: 6, title: 'Stylish Loft', location: 'San Francisco', price: 800000, bedrooms: 2, bathrooms: 2, parking: 1, image: require('../assets/6.jpeg') },
  ]);

  const [filteredProperties, setFilteredProperties] = useState(properties);

  const handleSearch = (searchCriteria) => {
    const { location, minPrice, maxPrice, propertyType, bedrooms, bathrooms, parking } = searchCriteria;

    const filtered = properties.filter(property => {
      const matchesLocation = location ? property.location === location : true;
      const matchesMinPrice = minPrice ? property.price >= Number(minPrice) : true;
      const matchesMaxPrice = maxPrice ? property.price <= Number(maxPrice) : true;
      const matchesBedrooms = bedrooms ? property.bedrooms >= Number(bedrooms) : true;
      const matchesBathrooms = bathrooms ? property.bathrooms >= Number(bathrooms) : true;
      const matchesParking = parking ? property.parking >= Number(parking) : true;

      return matchesLocation && matchesMinPrice && matchesMaxPrice && matchesBedrooms && matchesBathrooms && matchesParking;
    });

    setFilteredProperties(filtered);
  };

  return (
    <Router>
      <section className="combined-listings">
        <h2>Properties</h2>
        <SearchBar onSearch={handleSearch} />
        <div className="listing-cards">
          <PropertyList properties={filteredProperties} />
        </div>
        <Switch>
          <Route path="/property/:id">
            <PropertyDetails properties={properties} />
          </Route>
        </Switch>
      </section>
    </Router>
  );
};

export default PropertyListContainer;
