import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import '../styles/PropertyList.css'; // Use the same styles for consistency
import { FaHeart } from 'react-icons/fa';
import { WishlistContext } from '../context/WishlistContext'; // Make sure to import it correctly

const MoreProperties = () => {
  // Mock data for additional properties with images
  const additionalProperties = [
    { id: 1, title: 'Modern Villa', location: 'Los Angeles', price: 500000, image: require('../assets/image build.jpeg') },
    { id: 2, title: 'Cozy Apartment', location: 'New York', price: 300000, image: require('../assets/download (3).jpeg') },
    { id: 3, title: 'Luxury House', location: 'Miami', price: 400000, image: require('../assets/download (4).jpeg') },
    { id: 4, title: 'Spacious Condo', location: 'Seattle', price: 750000, image: require('../assets/download (5).jpeg') },
    { id: 5, title: 'Charming Bungalow', location: 'Chicago', price: 650000, image: require('../assets/download (6).jpeg') },
    { id: 6, title: 'Stylish Loft', location: 'San Francisco', price: 800000, image: require('../assets/download (7).jpeg') },
  ];

  const { addToWishlist } = useContext(WishlistContext); // Use useContext to access the wishlist functions

  const handleAddToWishlist = (property) => {
    addToWishlist(property);
    alert(`${property.location} has been added to your wishlist!`);
  };

  return (
    <div>
      <h2>More Properties</h2>
      <div className="property-list">
        {additionalProperties.map((property) => (
          <div className="property-card" key={property.id}>
            <img src={property.image} alt={property.location} className="property-image" /> {/* Added image */}
            <h3>{property.location}</h3>
            <p>${property.price.toLocaleString()}</p>
            <p>Parking Type: {property.parkingType}</p>
            <button onClick={() => handleAddToWishlist(property)}>
              <FaHeart /> Add to Wishlist
            </button>
          </div>
        ))}
      </div>
      <Link to="/" className="view-more-button">
        Back to Home
      </Link>
    </div>
  );
};

export default MoreProperties;
