import React, { useContext } from 'react';
import '../styles/PropertyList.css'; // Import CSS for styling
import { Link } from 'react-router-dom'; // Import Link for navigation
import { FaHeart } from 'react-icons/fa'; // Import heart icon for wishlist button

const PropertyList = ({ properties }) => {
 

  // Handle adding property to wishlist
  const handleAddToWishlist = (property) => {
    addToWishlist(property); // Add property to wishlist
    alert(`${property.title} in ${property.location} has been added to your wishlist!`);
  };

  return (
    <div className="property-list">
      
      {properties.map((property) => (
        <div className="property-card" key={property.id}>
          <img src={property.image} alt={property.title} className="property-image" /> {/* Added className for image styling */}
          <h3>{property.title}</h3> {/* Display property title */}
          <p>{property.location}</p> {/* Display property location */}
          <p>${property.price.toLocaleString()}</p> {/* Display formatted property price */}
          {/* Optionally show parking type if available */}
          {property.parkingType && <p>Parking Type: {property.parkingType}</p>}
          <button className="wishlist-button" onClick={() => handleAddToWishlist(property)}>
            <FaHeart /> Add to Wishlist
          </button>
        </div>
      ))}
      {/* "View More Properties" button */}
      <div className="view-more">
        <Link to="/more-properties" className="view-more-button">
          View More Properties
        </Link>
      </div>
    </div>
  );
};

export default PropertyList;
