import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import PropertyList from './PropertyList';
import SearchBar from './SearchBar';
import PropertyDetails from './PropertyDetails';

const PropertyListContainer = () => {
  const [properties] = useState([
    { id: 1, title: 'Modern Villa', location: 'Los Angeles', price: 500000, bedrooms: 3, bathrooms: 2, parking: 2, image: require('../assets/1.jpeg'), propertyType: 'Plot' },
    { id: 2, title: 'Cozy Apartment', location: 'New York', price: 300000, bedrooms: 2, bathrooms: 1, parking: 1, image: require('../assets/2.jpeg'), propertyType: 'Apartment' },
    { id: 3, title: 'Luxury House', location: 'Miami', price: 400000, bedrooms: 4, bathrooms: 3, parking: 2, image: require('../assets/3.jpeg'), propertyType: 'House' },
    { id: 4, title: 'Spacious Condo', location: 'Seattle', price: 750000, bedrooms: 3, bathrooms: 2, parking: 2, image: require('../assets/4.jpeg'), propertyType: 'Condo' },
    { id: 5, title: 'Charming Bungalow', location: 'Chicago', price: 650000, bedrooms: 3, bathrooms: 1, parking: 1, image: require('../assets/5.jpeg'), propertyType: 'Bungalow' },
    { id: 6, title: 'Stylish Loft', location: 'San Francisco', price: 800000, bedrooms: 2, bathrooms: 2, parking: 1, image: require('../assets/6.jpeg'), propertyType: 'Loft' },
  ]);

  const [filteredProperties, setFilteredProperties] = useState(properties);

  const handleSearch = (searchCriteria) => {
    const { location, minPrice, maxPrice, propertyType, bedrooms, bathrooms, parking } = searchCriteria;

    const filtered = properties.filter(property => {
      const matchesLocation = location ? property.location === location : true;
      const matchesMinPrice = minPrice ? property.price >= Number(minPrice) : true;
      const matchesMaxPrice = maxPrice ? property.price <= Number(maxPrice) : true;
      const matchesBedrooms = bedrooms ? property.bedrooms >= Number(bedrooms) : true;
      const matchesBathrooms = bathrooms ? property.bathrooms >= Number(bathrooms) : true;
      const matchesParking = parking ? property.parking >= Number(parking) : true;
      const matchesPropertyType = propertyType ? property.propertyType === propertyType : true; // Add property type check

      return matchesLocation && matchesMinPrice && matchesMaxPrice && matchesBedrooms && matchesBathrooms && matchesParking && matchesPropertyType;
    });

    setFilteredProperties(filtered);
  };

  return (
    <Router>
      <section className="combined-listings">
        <h2>Properties</h2>
        <SearchBar onSearch={handleSearch} />
        <div className="listing-cards">
          <PropertyList properties={filteredProperties} />
        </div>
        <Routes>
          <Route path="/property/:id" element={<PropertyDetails properties={properties} />} />
        </Routes>
      </section>
    </Router>
  );
};

export default PropertyListContainer;
