import React, { useState } from 'react';
import '../styles/SearchBar.css';

const SearchBar = ({ onSearch }) => {
  const [location, setLocation] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [propertyType, setPropertyType] = useState('');
  const [bedrooms, setBedrooms] = useState('');
  const [bhks, setBhks] = useState('');

  const handleSearch = () => {
    // Pass all filter values back to the parent component
    onSearch({ location, minPrice, maxPrice, propertyType, bedrooms, bhks });
  };

  return (
    <div className="search-bar">
      <select
        value={location}
        onChange={(e) => setLocation(e.target.value)}
        placeholder="Select Location"
      >
        <option value="">Select Location</option>
        <option value="Khammam">Khammam</option>
        <option value="Nellore">Nellore</option>
        <option value="Hyderabad">Hyderabad</option>
      </select>

      <div className="price-range">
        <input
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
      </div>

      <select
        value={propertyType}
        onChange={(e) => setPropertyType(e.target.value)}
      >
        <option value="">Select Property Type</option>
        <option value="Plot">Plot</option>
        <option value="Commercial">Commercial</option>
        <option value="Apartment">Apartment/House</option>
      </select>

      {/* Conditionally render bedrooms and BHK inputs */}
      {propertyType === 'Apartment' && (
        <>
          <input
            type="number"
            placeholder="Number of Bedrooms"
            value={bedrooms}
            onChange={(e) => setBedrooms(e.target.value)}
          />

          <select
            value={bhks}
            onChange={(e) => setBhks(e.target.value)}
          >
            <option value="">Select BHK</option>
            <option value="1 BHK">1 BHK</option>
            <option value="2 BHK">2 BHK</option>
            <option value="3 BHK">3 BHK</option>
            <option value="4 BHK">4 BHK</option>
            <option value="5 BHK">5 BHK</option>
          </select>
        </>
      )}

      <button onClick={handleSearch}>Search</button>
    </div>
  );
};

export default SearchBar;
